import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';
import Chart from "./Chart/Chart";

function App() {
  const [data, setData] = useState<{ date: string; open: number; high: number; low: number; close: number }[]>([]);

  useEffect(() => {
    fetch("/nikkei_hist.csv")
      .then(response => response.text())
      .then(data => {
        const parsedData = data
          .split("\n")
          .map(row => {
            const columns = row.replace(/"/g, "").split(",");
            // 日付がYYYY/MM/DD形式かつ値が数値のものだけ
            if (/^\d{4}\/\d{2}\/\d{2}$/.test(columns[0]) &&
                !isNaN(parseFloat(columns[1])) &&
                !isNaN(parseFloat(columns[2])) &&
                !isNaN(parseFloat(columns[3])) &&
                !isNaN(parseFloat(columns[4]))) {
              return {
                date: columns[0],
                open: parseFloat(columns[2]),
                high: parseFloat(columns[3]),
                low: parseFloat(columns[4]),
                close: parseFloat(columns[1])
              };
            }
            return null;
          })
          .filter((row): row is { date: string; open: number; high: number; low: number; close: number } => row !== null)
          .slice(-365); // 過去1年分
        setData(parsedData);
      });
  }, []);

  return (
    <div className="App">
      <Chart data={data} />
    </div>
  );
}

export default App;
