import React, { useState, useMemo } from "react";
import { XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer, ComposedChart, Bar } from "recharts";
import PeriodSelector from "./PeriodSelector";

interface DataPoint {
  date: string;
  open: number;
  high: number;
  low: number;
  close: number;
}

interface ChartProps {
  data: DataPoint[];
}

const getPeriodCount = (period: string) => {
  switch (period) {
    case "1y":
      return 365;
    case "6m":
      return 183;
    case "3m":
      return 92;
    case "1m":
      return 31;
    case "1w":
      return 7;
    default:
      return 365;
  }
};

const Candle = (props: any) => {
  const { x, y, width, height, fill } = props;
  return <rect x={x} y={y} width={width} height={height} fill={fill} stroke="#333" />;
};

const Chart: React.FC<ChartProps> = ({ data }) => {
  const [period, setPeriod] = useState<string>("1y");

  const filteredData = useMemo(() => {
    const count = getPeriodCount(period);
    return data.slice(-count);
  }, [data, period]);

  // ローソク足用のカスタムBarデータ
  const renderCandles = (props: any) => {
    const { x, y, width, height, payload } = props;
    const color = payload.close > payload.open ? "#e74c3c" : "#2ecc71";
    const candleY = Math.min(y(payload.open), y(payload.close));
    const candleHeight = Math.abs(y(payload.open) - y(payload.close));
    return (
      <g>
        {/* ヒゲ */}
        <line x1={x + width / 2} x2={x + width / 2} y1={y(payload.high)} y2={y(payload.low)} stroke={color} strokeWidth={2} />
        {/* 実体 */}
        <rect
          x={x}
          y={candleY}
          width={width}
          height={candleHeight === 0 ? 1 : candleHeight}
          fill={color}
          stroke="#333"
        />
      </g>
    );
  };

  return (
    <div style={{ width: "100%", height: 400 }}>
      <h2>日経平均株価（ローソク足・期間選択可）</h2>
      <PeriodSelector period={period} onChange={setPeriod} />
      <ResponsiveContainer width="100%" height="85%">
        <ComposedChart data={filteredData} margin={{ top: 20, right: 30, left: 0, bottom: 0 }} barCategoryGap={2}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" minTickGap={40} />
          <YAxis domain={["auto", "auto"]} />
          <Tooltip />
          <Bar
            dataKey="close"
            shape={renderCandles}
            minPointSize={1}
            maxBarSize={10}
            isAnimationActive={false}
          />
        </ComposedChart>
      </ResponsiveContainer>
    </div>
  );
};

export default Chart;
